import React, {useState} from "react";
import {secondsToDuration} from '../utils/duration';

function FocusTime() {
  const [focusTime, setFocusTime] = useState(1500);

  function addTime() {
    setFocusTime(prevTime => prevTime + 300)
  }

  function reduceTime() {
    setFocusTime(prevTime => prevTime - 300)
  }

  return <span>{`Focus Duration: {secondsToDuration(focusTime)}`}</span>

}


export default FocusTime;
